 @parCodLoja [nvarchar] (256),
 @parOrderBy [nvarchar] (256)
)
AS
SET NOCOUNT ON
SET ANSI_WARNINGS OFF

DECLARE @Query VARCHAR(5000)
DECLARE @Where VARCHAR(5000)

SET @Query = ' select 
pNUMERO_NF,
pDATA_EMISSAO,
pPRODUTO,
pDESCRICAO,
pLOJA,
pCLIENTE,
pNOME_CLIENTE,
sum(pQUANTIDADE) pQUANTIDADE,
sum(pQTD_EM_CAIXA) pQTD_EM_CAIXA,
sum(pVLR_TOTAL_NOTA) pVLR_TOTAL_NOTA,
sum(pVLR_TOTAL_PRODUTO) pVLR_TOTAL_PRODUTO
from AviacaoVendas ';

SET @Where  = ' WHERE 
				pNUMERO_NF IN (select TOP 3 
				a.pNUMERO_NF
				from AviacaoVendas a where
				a.pDATA_EMISSAO >= dateadd(day,-60, cast(getdate() as date)) ';

				SET @Where  = CONCAT(@Where, CONCAT( ' and a.pCLIENTE = ', char(39) + @parCodCliente + char(39) ));
				SET @Where  = CONCAT(@Where, CONCAT( ' and a.pLOJA = ', char(39) + @parCodLoja + char(39) ));

				SET @Where  = CONCAT(@Where, ' group by
					a.pNUMERO_NF,
					a.pDATA_EMISSAO
					ORDER BY a.pDATA_EMISSAO DESC) ');

SET @Where  = CONCAT(@Where, CONCAT( ' and pLOJA =  ', char(39) + @parCodLoja + char(39) ));

--IF (@parOrderBY is not null)
-- SET @Where  = CONCAT(@Where, CONCAT(' ORDER BY ', @parOrderBY));
	
SET @Where  = CONCAT(@Where, ' GROUP BY pNUMERO_NF,
pDATA_EMISSAO,
pPRODUTO,
pDESCRICAO,
pLOJA,
pCLIENTE,
pNOME_CLIENTE');
    SET @Query = @Query + @Where;

  EXECUTE (@query)
