CREATE PROCEDURE [dbo].[AviacaoViagemMov_LoadByParametros]
(
 @parDataSaidaIni [nvarchar] (256) = NULL,
 @parDataSaidaFim [nvarchar] (256) = NULL,
 @parDataPrevIni [nvarchar] (256) = NULL,
 @parDataPrevFim [nvarchar] (256) = NULL,
