DECLARE @Where VARCHAR(5000)

SET @Query = 'select 
a.pid, 
a.pDATA_SAIDA, 
a.pDATA_PREV_RETORNO,  
a.pDATA_RETORNO, 
a.pRODIZIO, 
a.pSTATUS, 
a.pMOTORISTA_ID,  
a.pMOTORISTA_CODIGO,
a.pMOTORISTA_NOME, 
a.pCAMINHAO_ID, 
a.pCAMINHAO_PLACA, 
a.pCAMINHAO_TIPO, 
a.pCAMINHAO_DESCRICAO, 
a.pCAMINHAO_CAPACIDADE_TON, 
a.pCAMINHAO_CAPACIDADE_PALETE, 
a.pCAMINHAO_STATUS,
   (SELECT STUFF((SELECT  ' + char(39) + '/' + char(39) +  ' + b.pLOCAL_LINHA_DESCRICAO   
FROM vaviacaoviagemmovaviacaoviagemmovviewgrid b  where b.pId = a.pId GROUP BY b.pLOCAL_LINHA_DESCRICAO 
FOR XML PATH (' +char(39) + char(39) + ')) , 1, 1, ' + char(39) + char(39) + ')) as pLOCAL_LINHA_DESCRICAO, 
   (SELECT STUFF((SELECT  ' + char(39) + '/' + char(39) +  ' + c.pLOCAL_LINHA_RETORNO_DESCRICAO   
FROM vaviacaoviagemmovaviacaoviagemmovviewgrid c  where c.pId = a.pId GROUP BY c.pLOCAL_LINHA_RETORNO_DESCRICAO 
FOR XML PATH (' +char(39) + char(39) + ')) , 1, 1, ' + char(39) + char(39) + ')) as pLOCAL_LINHA_RETORNO_DESCRICAO  
from vaviacaoviagemmovaviacaoviagemmovviewgrid a';
SET @Where  = ' WHERE 0=0 ';
	
-- ** início teste da data de saida do veiculo
	IF @parDataSaidaIni IS NOT NULL and @parDataSaidaFim IS NULL
		BEGIN   
			SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDATA_SAIDA) >= ', char(39) + @parDataSaidaIni + char(39) ));
		END;
	ELSE IF @parDataSaidaIni IS NULL and @parDataSaidaFim  IS NOT NULL
        BEGIN
			SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDATA_SAIDA) <= ', char(39) + @parDataSaidaFim + char(39) ));
 		END;
	ELSE IF @parDataSaidaIni IS NOT NULL and @parDataSaidaFim IS NOT NULL
	 	 BEGIN
		 		SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDATA_SAIDA) between ', char(39) + @parDataSaidaIni + char(39) ));
				SET @Where  = CONCAT(@Where, CONCAT( ' AND ', char(39) +  @parDataSaidaFim + char(39) ));		
		 END;	
-- ** fim teste da data de saida do veiculo 
	 
-- ** início teste da data de previsão de retorno
	IF @parDataPrevIni IS NOT NULL and @parDataPrevFim IS NULL
		BEGIN   
			SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDATA_PREV_RETORNO) >= ', char(39) + @parDataPrevIni + char(39) ));
		END;
	ELSE IF @parDataPrevIni IS NULL and @parDataPrevFim  IS NOT NULL
        BEGIN
			SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDATA_PREV_RETORNO) <= ', char(39) + @parDataPrevFim + char(39) ));
 		END;
	ELSE IF @parDataPrevIni IS NOT NULL and @parDataPrevFim IS NOT NULL
	 	 BEGIN
		 		SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDATA_PREV_RETORNO) between ', char(39) + @parDataPrevIni + char(39) ));
				SET @Where  = CONCAT(@Where, CONCAT( ' AND ', char(39) +  @parDataPrevFim + char(39) ));		
		 END;	
-- ** fim teste da data de previsão de retorno	 

-- ** início teste da data de retorno
IF @parDataRetIni IS NOT NULL and @parDataRetFim IS NULL
		BEGIN   
			SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDATA_RETORNO) >= ', char(39) + @parDataRetIni + char(39) ));
		END;
	ELSE IF @parDataRetIni IS NULL and @parDataRetFim  IS NOT NULL
        BEGIN
			SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDATA_RETORNO) <= ', char(39) + @parDataRetFim + char(39) ));
 		END;
	ELSE IF @parDataRetIni IS NOT NULL and @parDataRetFim IS NOT NULL
	 	 BEGIN
		 		SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDATA_RETORNO) between ', char(39) + @parDataRetIni + char(39) ));
				SET @Where  = CONCAT(@Where, CONCAT( ' AND ', char(39) +  @parDataRetFim + char(39) ));		
		 END;		
-- ** fim teste da data de movimentação do titulomov

    IF (@parCaminhaoId is not null and @parCaminhaoId <> 0 )
        SET @Where  = CONCAT(@Where, CONCAT(' AND a.pCAMINHAO_ID = ', @parCaminhaoId));
	IF (@parMotoristaId is not null and @parMotoristaId <>0 )
        SET @Where  = CONCAT(@Where, CONCAT(' AND a.pMOTORISTA_ID = ', @parMotoristaId));
	IF (@parLocalLinhaId is not null and @parLocalLinhaId <> '' )
        SET @Where  = CONCAT(@Where, CONCAT( ' AND a.pLOCAL_LINHA_DESCRICAO like ', char(39) + @parLocalLinhaId + char(39)));
	
	IF (@parFlgCaminhaoEmViagem = 'S' )
        SET @Where  = CONCAT(@Where, ' AND a.pDATA_RETORNO IS NULL ');

	--IF (@parOrderBY is not null)
      -- SET @Where  = CONCAT(@Where, CONCAT(' ORDER BY ', @parOrderBY));
	
	SET @Where  = CONCAT(@Where, ' GROUP BY a.pid, 
a.pDATA_SAIDA, 
a.pDATA_PREV_RETORNO,  
a.pDATA_RETORNO, 
a.pRODIZIO, 
a.pSTATUS, 
a.pMOTORISTA_ID,  
a.pMOTORISTA_CODIGO,
a.pMOTORISTA_NOME, 
a.pCAMINHAO_ID, 
a.pCAMINHAO_PLACA, 
a.pCAMINHAO_TIPO, 
a.pCAMINHAO_DESCRICAO, 
a.pCAMINHAO_CAPACIDADE_TON, 
a.pCAMINHAO_CAPACIDADE_PALETE, 
a.pCAMINHAO_STATUS');
    SET @Query = @Query + @Where;

  EXECUTE (@query)
RETURN
GO

