CREATE PROCEDURE [dbo].[MovimentacaoLote_LoadViewParametros]
(
 @parDataFabIni [nvarchar] (256) = NULL,
 @parDataFabFim [nvarchar] (256) = NULL,
 @parDataMatIni [nvarchar] (256) = NULL,
 @parDataMatFim [nvarchar] (256) = NULL,
 @parProdutoId [int] = NULL,
 @parFlgFinalizado [nvarchar] (256) = NULL,
 @parOrderBy [nvarchar] (256)
)
AS
SET NOCOUNT ON
SET ANSI_WARNINGS OFF

DECLARE @Query VARCHAR(5000)
DECLARE @Where VARCHAR(5000)

SET @Query = 'select * from vMovimentacaoLoteMovimentacaoLoteViewGrid a';
SET @Where  = ' WHERE 0=0 ';
	
-- ** início teste da data de fabricação
	IF @parDataFabIni IS NOT NULL and @parDataFabFim IS NULL
		BEGIN   
			SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDataFabricacao) >= ', char(39) + @parDataFabIni + char(39) ));
		END;
	ELSE IF @parDataFabIni IS NULL and @parDataFabFim  IS NOT NULL
        BEGIN
			SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDataFabricacao) <= ', char(39) + @parDataFabFim + char(39) ));
 		END;
	ELSE IF @parDataFabIni IS NOT NULL and @parDataFabFim IS NOT NULL
	 	 BEGIN
		 		SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDataFabricacao) between ', char(39) + @parDataFabIni + char(39) ));
				SET @Where  = CONCAT(@Where, CONCAT( ' AND ', char(39) +  @parDataFabFim + char(39) ));		
		 END;	
-- ** fim teste da data de fabricacao 
	 
-- ** início teste da data de maturacao
	IF @parDataMatIni IS NOT NULL and @parDataMatFim IS NULL
		BEGIN   
			SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDataMaturacao) >= ', char(39) + @parDataMatIni + char(39) ));
		END;
	ELSE IF @parDataMatIni IS NULL and @parDataMatFim  IS NOT NULL
        BEGIN
			SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDataMaturacao) <= ', char(39) + @parDataMatFim + char(39) ));
 		END;
	ELSE IF @parDataMatIni IS NOT NULL and @parDataMatFim IS NOT NULL
	 	 BEGIN
		 		SET @Where  = CONCAT(@Where, CONCAT( ' AND CONVERT(Date,a.pDataMaturacao) between ', char(39) + @parDataMatIni + char(39) ));
				SET @Where  = CONCAT(@Where, CONCAT( ' AND ', char(39) +  @parDataMatFim + char(39) ));		
		 END;	
-- ** fim teste da data de maturacao	 

    IF (@parProdutoId is not null and @parProdutoId <> 0 )
        SET @Where  = CONCAT(@Where, CONCAT(' AND a.pProdutoId = ', @parProdutoId));
		
	IF (@parFlgFinalizado = 'N' )
        SET @Where  = CONCAT(@Where, ' AND a.pFinalizado = ' + char(39) + 'N' + char(39));

	--IF (@parOrderBY is not null)
      -- SET @Where  = CONCAT(@Where, CONCAT(' ORDER BY ', @parOrderBY));
		
    SET @Query = @Query + @Where;

  EXECUTE (@query)
RETURN
GO

